import paramikoclass SSHClient:    def __init__(self, host, port, username, password):        self.host = host        self.port = port        self.username = username        self.password = password        self._client = False    def __exit__(self, *args):        if self._client:            self.close()    def connect(self):        self._client = paramiko.SSHClient()        self._client.set_missing_host_key_policy(paramiko.AutoAddPolicy())        self._client.connect(self.host, port=self.port, username=self.username, password=self.password)    def execute_command(self, command):        stdin, stdout, stderr = self._client.exec_command(command)        return stdout.read().decode()    def close(self):        self._client.close()        self._client = False# 示例用法if __name__ == "__main__":    ssh_client = SSHClient("10.20.69.58", 22, "root", "jaguar")    try:        ssh_client.connect()        result = ssh_client.execute_command("ls -l")        print(result)    finally:        ssh_client.close()